<style>
  .uforpc-ufo-icon-animate {
    --animation-duration: 2s;

    .ufo-middle-left {
      animation: logoBlink var(--animation-duration) ease-in-out infinite;
      animation-delay: 0s;
    }

    .ufo-middle-center {
      animation: logoBlink var(--animation-duration) ease-in-out infinite;
      animation-delay: calc(var(--animation-duration) / 3);
    }

    .ufo-middle-right {
      animation: logoBlink var(--animation-duration) ease-in-out infinite;
      animation-delay: calc(var(--animation-duration) * 2 / 3);
    }
  }

  @keyframes logoBlink {
    0%,
    100% {
      fill: transparent;
    }
    50% {
      fill: #fbbf24;
    }
  }
</style>
